body {
    background: #fff;
    min-width: 315px;
    overflow-x: hidden;
    font-family: Georgia, serif;
    font-size: 100%;
    line-height: 1.5;
    color: #333;
}

main {
    max-width: 700px;
    margin: 0 auto
}

p {
    padding: 0;
    margin: 0
}

.h1, h1 {
    font-size: 3rem;
    line-height: 3.5rem
}

.h2, h2 {
    font-size: 2rem;
    line-height: 2.5rem
}

.h3, h3 {
    font-size: 1.5rem;
    line-height: 2rem
}

.h4, h4 {
    font-size: 1.125rem;
    line-height: 1.5rem
}

.h5, h5 {
    font-size: .875rem;
    line-height: 1.125rem
}

.h6, h6 {
    font-size: .75rem;
    line-height: 1rem
}

h1, h2, h3, h4, h5, h6 {
    margin: 0;
    padding: 0;
    font-weight: 400;
    letter-spacing: .06em
}

p {
    font-size: 1rem;
    font-weight: 300;
    margin-right: 1em;
    margin-left: 1em;
    margin-top: 0;
    margin-bottom: 0.5rem;
}

.slide {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    width: 100%;
}

.slide .caption {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    padding: 8px;
    background: rgba(0, 0, 0, 0.6);
    color: #ddd;
}

.carousel {
    display: flex;
    align-items: flex-end;
    justify-content: center;
}

.carousel .carousel-item {
    width: 400px;
    overflow: hidden;
    text-overflow: ellipsis;
}

a {
    text-decoration: none;
}

.ampize-primary-color, a.ampize-primary-color {
    color: #2196F3;
}

.ampize-paging {
    margin-bottom: 10px;
}

.ampize-paging button.ampstart-btn > a {
    text-decoration: none;
}

.ampize-paging-current-page {
    vertical-align: middle;
    line-height: 40px;
    margin: 0 30px 0 30px;
}

.ampize-menu-logo {
    margin-top:6px;
    margin-right:6px;
    background: no-repeat center;
    background-size: contain;
    width: 150px;

}

.ampize-header {
    top: 0;
    left: 0;
    height: 80px;
    padding: 0 10px;
    box-shadow: 0 0 40px 0 rgba(0, 0, 0, 0.1);
    width: 100%;
    backface-visibility: hidden;
    transform: translateY(0%);
    transition: all .4s ease;
    background: linear-gradient(to bottom, #005689, #00558c);
    z-index: 1000;
    font-family: "Guardian Egyptian Headline";
    font-weight: 500;
}

.ampize-header a {
    text-decoration: none;
}

.ampize-menu-tabs {
    margin-left: 60px
}

.ampize-menu-tab {
    padding: 6px 20px;
    color: #333;
    margin: 0 4px;
    position: relative;
    transition: .1s ease color;
    color: white;
}

.ampize-menu-tab > a {
    display: block;
    margin: auto;
    z-index: 10;
    height: 34px;
    padding-top: 2px;
    color: white;
    font-size: 22px;
    text-decoration: none;
}

.ampize-menu-tab.active {
    color: #4BC6DF;
}

.ampize-menu-sidebar-item.active {
    border-top: 1px solid #4BC6DF;
    color: #4BC6DF;
}

.ampize-menu-tab::before {
    bottom: 0;
    box-shadow: 0 0 40px 0 rgba(0, 0, 0, 0.1);
    content: '';
    left: -10px;
    opacity: 0;
    position: absolute;
    right: -10px;
    top: 0;
    transition: opacity .2s ease-out;
}

.ampize-menu-tab::after {
    background: #2196F3;
    bottom: 0;
    content: '';
    height: 3px;
    left: 20px;
    margin: auto;
    opacity: 0;
    position: absolute;
    right: 20px;
    transition: .1s ease opacity;
}

.ampize-menu-tab.active::after {
    opacity: 1;
}

.ampize-menu-tab.active:hover::after {
    opacity: 0;
}

.ampize-menu-tab .ampize-menu-tab-list {
    opacity: 0;
    pointer-events: none;
    position: absolute;
    left: -10px;
    top: 100%;
    transition: .2s ease-out;
}

.ampize-menu-tab:hover .ampize-menu-tab-list {
    opacity: 1;
    pointer-events: all;
    z-index: 0;
}

.ampize-menu-tab .ampize-menu-tab-list-shadow {

}

.ampize-menu-tab .ampize-menu-tab-list-shadow::before {
    bottom: 50px;
    box-shadow: 0 0 40px 0 rgba(0, 0, 0, 0.1);
    content: '';
    left: 50px;
    position: absolute;
    right: 50px;
    top: 0;
}

.ampize-menu-tab-list > ul {
    margin: 0;
    padding: 0;
    list-style: none;
    min-width: 250px;
    position: relative;
    background: #dfdfdf;
}

.ampize-menu-tab-list > ul li {
    box-sizing: border-box;
    position: relative;
}

.ampize-menu-tab-list > ul li:hover {

}

.ampize-menu-tab-list > ul li a {
    color: #333;
    font-size: 16px;
    line-height: 26px;
    display: block;
    padding: 7.5px 30px;
}

.ampize-sidebar-trigger {
    position: absolute;
    z-index: 2;
    cursor: pointer;
    bottom: -8px;
    left: 1em;
    top: auto;
    height: 54px;
    width: 54px;
    border: 0;
    outline: none;
    border-radius: 50%;
    background-color: #4bc6df;
    box-shadow: 0 0 0 0.0625rem rgba(0,0,0,0.08);
    font: inherit;
    color: white;
    font-size: 36px;
    padding: 12px;
    line-height: 26px;
}

amp-sidebar {
    background: #00456E;
    width: 80vw;
    max-width: 300px;
}

amp-sidebar a, amp-sidebar a:active, amp-sidebar a:visited {
    color: white;
}

.ampize-menu-sidebar-layer {
    height: 100%;
    position: relative;
}

.ampize-menu-sidebar-close {
    position: absolute;
    right: 10px;
    top: 15px;
    z-index: 2;
    height: 54px;
    width: 54px;
    border: 0;
    outline: none;
    border-radius: 50%;
    background-color: #4bc6df;
    box-shadow: 0 0 0 0.0625rem rgba(0,0,0,0.08);
    font: inherit;
    color: white;
    font-size: 36px;
    padding: 0px 12px 12px 12px;
}

.ampize-menu-sidebar-items {
    left: 0;
    overflow-x: hidden;
    overflow-y: hidden;
    position: absolute;
    right: 0;
    bottom: 80px;
    top: 80px;
    padding-left: 18px;
    font-family: "Guardian Egyptian Headline";
    font-weight: 500;
}

.ampize-menu-sidebar-item {
    border-top: 1px solid #005689;
    display: block;
    position: relative;
    text-transform: none;
    padding-top: 10px;
    padding-bottom: 10px;
    font-size: 22px;
    color: white;    
}

.ampize-menu-sidebar-item-expand {
    position: absolute;
    right: 20px;
    font-size: 22px;
    color: white; 
}

a.ampize-menu-sidebar-item {
    text-decoration: none;
}

.ampize-menu-sidebar-item>input[type="checkbox"] {
    position: absolute;
    visibility: hidden;
}

.ampize-menu-sidebar-item .ampize-menu-sidebar-submenu {
    background: #00456e;
    bottom: 0;
    box-shadow: 0 3px 20px 0 rgba(0,0,0,0.075);
    left: 0;
    position: fixed;
    right: 0;
    top: 0;
    transform: translateX(100%);
    transition: transform 233ms cubic-bezier(0, 0, 0.21, 1);
    z-index: 2;
}

.ampize-menu-sidebar-item input:checked~.ampize-menu-sidebar-submenu {
    transform: translateX(0);
}

.ampize-menu-sidebar-submenu-return {
    color: white;
    left: 25px;
    position: absolute;
    top: 25px;
    line-height: 25px;
    font-size: 22px;
}

.ampize-menu-sidebar-submenu-items {
    left: 0;
    overflow-x: hidden;
    overflow-y: hidden;
    position: absolute;
    right: 0;
    bottom: 0;
    top: 80px;
    color: rgb(74, 74, 74);
    padding-left:18px;
}

.ampize-menu-sidebar-submenu-item {

}

.ampize-menu-sidebar-submenu-item.ampize-menu-sidebar-item:first-child {
    border-top: none;
    color: #4BC6DF;
}

.ampize-btn {
    font-family: inherit;
    font-weight: inherit;
    font-size: 1rem;
    line-height: 1.125rem;
    padding: .7em .8em;
    text-decoration: none;
    white-space: nowrap;
    word-wrap: normal;
    vertical-align: middle;
    cursor: pointer;
    background-color: #000;
    color: #fff;
    border: 1px solid #fff;
}

.ampize-btn > a {
    text-decoration: none;
}

.ampize-btn-secondary {
    background-color: #fff;
    color: #000;
    border: 1px solid #000;
}

.ampize-header ~ #content {
    padding-top: 85px;
}

@media only screen and (max-width: 52rem){
    .ampize-header {
        top: -1px;
        left: -1px;
        width: calc(100% + 2px);
        display: flex;
        justify-content: flex-end;
    }

    .ampize-nav-left {
        flex: 1;
        justify-content: flex-end;
    }

    .ampize-menu-logo {
        width: 240px;
    }

    .ampize-header ~ #content {
        padding-top: 90px;
    }
}

.sr-card {

  text-rendering: geometricPrecision; /* slow but nice...might decide to remove again */
  line-height: 1.4;

  /* Layout */
  display: flex;
  position: relative;
  box-sizing: border-box;
  overflow: hidden;

  /* Styling */
  cursor: pointer;
  background-color: #f6f6f6;
  box-shadow: 0px 2px 9px rgba(0, 0, 0, 0.18);
  color: #fff;
  margin: 1em;
  border-top: 1px solid #4BC6DF;
  font-family:"Guardian Egyptian Headline";
  font-weight: 500;

}

.sr-card .sr-inner {
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
    padding: 1em;
    position: relative;
    z-index: 3;
    color: inherit;
    text-decoration: inherit;
    outline: 0;
}

.sr-card .sr-inner h2 {
      margin: 3em 0 0 0;
      font-weight: 500;
      background: rgba(0, 0, 0, 0.57);
      padding: 0.5em 0.3em;
      font-size: 1.4em;
      line-height: 1.4;
}

.sr-card .sr-inner  p {
      margin: 1em 0;
      height: 4em ;
      font-weight: normal ;
      margin-bottom: -5em;
      overflow: hidden;
      text-overflow: ellipsis;
      flex-shrink: 0;
}

.sr-card::before {
    display: block;
    position: absolute;
    content: '';
    top: 0;
    left: 0;
    bottom: 0;
    right: -1px; /* fixes weird clipping bug in Chrome.. */
    background: linear-gradient(to top, rgb(9, 22, 33), rgba(0,0,0,0) 60%, rgba(0,0,0,0));
    z-index: 2;
    opacity: 0;
  }

.sr-card:first-child {
    min-height: 50vh;
}

.sr-card:first-child::before {
    opacity: 1;
}

